	public static Set<String> stopWordSet = new HashSet<String>(Arrays.asList(stopwords));
	
	public static boolean isStopword(String word) {
		if(word.length() < 2) return true;
		if(stopWordSet.contains(word)) return true;
		else return false;
	}
	
	public static String removeStopWords(String string) {
		String result = "";
		String[] words = string.split("\\s+");
		for(String word : words) {
			if(word.isEmpty()) continue;
			if(isStopword(word)) continue; //remove stopwords
			result += (word+" ");
		}
		return result;
	}
	
	public static int checkQuestionValidity(String query) {
		if (query.indexOf("Who") == 0) {
			return 1;
		} else if (query.indexOf("What") == 0) {
			return 2;
		} else if (query.indexOf("When") == 0) {
			return 3;
		} else if (query.indexOf("topic") == 0) {
			return 4;
		} else if (query.indexOf("db") == 0) {
			return 5;
		} else if (query.indexOf("search") == 0){
			return 6;
		} else {
			return -1;
		}
	}
	
	public static String readTextFiles() throws IOException {
		File folder = new File("./text-files");
		File[] listOfFiles = folder.listFiles();
		StringBuilder sb = new StringBuilder();
		String content = null;

		for (int i = 0; i < listOfFiles.length; i++) {
			File file = listOfFiles[i];
			if (file.isFile() && file.getName().endsWith(".txt")) {
				String line = null;

				try {
					// FileReader reads text files in the default encoding.
					FileReader fileReader = new FileReader(file);

					// Always wrap FileReader in BufferedReader.
					BufferedReader bufferedReader = new BufferedReader(
							fileReader);
					while ((line = bufferedReader.readLine()) != null) {
						sb.append(line);
					}
					sb.append(" "); // System.out.println(content);

					// Always close files.
					bufferedReader.close();
				} catch (FileNotFoundException ex) {
					System.out.println("Unable to open file '" + file + "'");
					ex.printStackTrace();
				} catch (IOException ex) {
					System.out.println("Error reading file '" + file + "'");
					// Or we could just do this:
					ex.printStackTrace();
				}
			}
		}
		content = sb.toString();

		// sentenceDetect used to get separate sentences for indexing
		return content;
	}
